    phantom.debug('format_3() called')

    template = """This is an automated message to inform you of the containment approval request in phantom, Mac Address {5} will be isolated by ISE. please login phantom and approve.  

Case Name: {0}
Case number: {1}
Device Hostname: {2}
Case Severity: {3}
Signature: {4}
Host MacAddress: {5}

Please do not respond to this message."""

    # parameter list for template variable replacement
    parameters = [
        "container:name",
        "container:id",
        "artifact:*.cef.deviceHostname",
        "container:severity",
        "artifact:*.cef.signature",
        "artifact:*.cef.host_mac",
    ]

    phantom.format(container=container, template=template, parameters=parameters, name="format_3", separator=", ")

    join_filter_2(container=container)

    return

